draw((0,2)--(1,2));
draw((0,3)--(1,3));
draw((0,4)--(1,4));
draw((1,0)--(1,2));
draw((2,0)--(2,2));
draw((3,0)--(3,2));
draw((4,0)--(5,0),EndArrow(4));
draw((0,0)--(-2,0),EndArrow(4));
label("$x$", (5,0), E);
draw((0,5)--(0,6),EndArrow(4));
draw((0,0)--(0,-1),EndArrow(4));
label("$y$", (0,6), N);[/asy]
