[asy]
unitsize(2 cm);

defaultpen(linewidth(1pt)+fontsize(10pt));
pair a,b,c,d;
b = (0,0);
c = (1,0);
d = c+dir(140);
a = d+dir(70);
draw(a--b--c--cycle);
draw(d--c);
pair s,t;
s = (.5,0)+(0,.05);
t = (.5,0)+(0,-.05);
draw(s--t);
s = .5*(c+d) + .05*dir(50);
t = .5*(c+d) - .05*dir(50);
draw(s--t);
