dot("$P$", P, N);
dot("$Q$", Q, dir(270));
dot("$R$", R, dir(270));
dot("$F'$", S, NW);
dot("$P'$", Pp, W);
dot("$Q'$", Qp, W);
dot("$R'$", Rp, W);
