draw(scale(1)*"$B$", (64, 157), r);
draw(scale(1)*"$C$", (165, 104), r);
draw(scale(1)*"$D$", (145, 20), r);
draw(scale(1)*"$E$", (34, -5), r);
draw(scale(1)*"$F$", (50, 109), r);
draw(scale(1)*"$G$", (95, 43), r);
draw(scale(1)*"1", (50, 100), r);
draw(scale(1)*"5", (48, 82), r);
